INSERT INTO `wp_posts` VALUES (14,1,'2022-02-23 14:49:49','0000-00-00 00:00:00','<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">私は今、侍大学工学研究科の環境システムAIに在籍しています。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">ここでの私の1日の生活について、紹介します。大学院では学部とは異なり、</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">研究室で過ごす時間がメインになります。そのため、</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">自分専用のデスクやPCだけでなく、冷蔵庫や電子レンジ、洗濯機、</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">お風呂といった、長時間過ごすために欠かせないものも揃っています。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">カフェテリアもあり、無料でお茶を飲んだりお菓子を食べたりできているので、</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">少し太ってしまいました。侍大学は勉強には最高の環境だと思います。</p>\n<!-- /wp:paragraph -->','大学院生　インタビュー男性-1、-2','','draft','open','open','','','','','2022-02-23 14:49:49','2022-02-23 14:49:49','',0,'http://samurai-university.local/?p=14',0,'post','',0);
